import React, { useState } from 'react';

export default function App() {
  const arr = useState(0);
  console.log(arr[0], arr[1]);

  /* 
     useState(0)
     useState会返回一个数组
     第一元素是当前的状态
     第二个元素是 一个函数，这个函数的作用就是设置修改状态
  
  */

  return (
    <div>
      <h2>{arr}</h2>
    </div>
  );
}
