import React, { useState } from 'react';

export default function App() {
  // 声明一个 state 的变量，赋值初始化，只在首次渲染的时候使用，如果去更新的话这个会被忽略
  const [state, setState] = useState(0);
  return (
    <div>
      <h1>{state}</h1>
      <button onClick={() => setState(state + 1)}>+1</button>
    </div>
  );
}
