import React, {useEffect, useState} from 'react'

/*  子组件  */
function Child() {
  const [count, setCount] = useState(0)
  useEffect(() => {
    console.log('子组件挂载了')
    return () => {
      console.log('子组件被销毁了')
    }
  }, [])
  return (
    <div>
      <h2>当前计数:{count}</h2>
      {/* <button onClick={}/> */}
    </div>
  )
}
export default function App() {
  
  
  return (
    <div>
       <Child />
    </div>
  )
}
