// 纯函数
let x = 1;

function add(a, b) {
  return a + b;
}
// 不是纯函数，x 是外部变量，依赖外部变量，而不是自己的参数
console.log(add(x, 10));

const obj = {};
function fun(b) {
  obj.a = 10;
  return b;
}

console.log(fun(5), obj);
