import React, { useState } from 'react';

export default function App() {
  // 声明一个 state 的变量，赋值初始化，只在首次渲染的时候使用，如果去更新的话这个会被忽略
  const [count, setCount] = useState(0);
  const increment = () => {
    setCount(count + 1);
  };
  return (
    <div>
      <h1>{count}</h1>
      <button onClick={increment}>+1</button>
    </div>
  );
}
