// 纯函数
let x = 1;

function add(a, b) {
  return a + b;
}
// 不是纯函数，x 是外部变量，依赖外部变量，而不是自己的参数
console.log(add(x, 10));

const obj = {};

function fun(b) {
  obj.a = 10;
  return b;
}
// 不是纯函数，改变了全局作用域的 obj，产生了副作用
console.log(fun(5), obj);

// 纯函数
let a = 1;
let b = 2;
/* 
 1. 他没有改变外部变量的值
 2. 每次调用的时候，传入的值相同，每次返回的结果也相同
 相同的输入得到相同的输出
*/
function sum(x, y) {
  return x + y;
}

console.log(sum(a, b));
