import React, { useState, useContext, createContext } from 'react';

// 创建一个 context

const context = createContext();
// 孙子组件
function GrandSon() {
  return <div>GrandSon 组件</div>;
}

// 孙子组件
function Son() {
  return (
    <div>
      <GrandSon />
    </div>
  );
}

function Father() {
  return <div>Father组件 </div>;
}

export default function App() {
  return (
    <div>
      <Father></Father>
    </div>
  );
}
