import React, { useState } from 'react';

function Son() {
  console.log('渲染了');
  return (
    <div>
      <h2>Son 组件</h2>
    </div>
  );
}
export default function App() {
  const [count, setCount] = useState(0);
  return (
    <div>
      <h1>{count}</h1>
      {/* 每次调用 setCount 都会重新渲染父组件，从而渲染子组件 */}
      <button onClick={() => setCount(count + 1)}>Add</button>
      <Son />
    </div>
  );
}
