import React, { useRef, useEffect } from 'react';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '张三',
    };
  }
  render() {
    return (
      <div>
        <h2>Header 组件</h2>
      </div>
    );
  }
  say = () => {
    console.log('我是 Header 组件方法 say');
  };
}
export default function App() {
  const inputRefs = useRef();
  useEffect(() => {});
  return (
    <div>
      <Header />
      <input type='text' ref={inputRefs} />
    </div>
  );
}
