import React, { useReducer } from 'react';

// 初始化状态
const initState = 10;
function reducer(state, action) {
  switch (action.type) {
    case 'increment':
      break;
    default:
      break;
  }
}
export default function App() {
  // useReducer 接受两个参数，第一个是 reducer 函数 第二个参数，初始值
  // const arr = useReducer(reducer, initState); // 0 function
  const [state, dispatch] = useReducer(reducer, initState); // 0 function

  // console.log(arr); // [0, ƒ]

  return (
    <div>
      <h1>initState:{state}</h1>
      {/* dispatch 用来接收一个 action 函数，reducer 函数中的 action */}
      <button>+</button>
      <button>-</button>
    </div>
  );
}
